CREATE TABLE v3_fps_messages_outgoing (rowkey VARCHAR NOT NULL PRIMARY KEY, fps.nostro_sort_code VARCHAR, fps.nostro_account_number VARCHAR, fps.amount FLOAT, fps.receiver_sort_code VARCHAR, fps.receiver_account_number VARCHAR, fps.receiver_name VARCHAR, fps.reference VARCHAR, fps.sender_name VARCHAR, fps.status VARCHAR, fps.created VARCHAR, fps.transaction_file_name VARCHAR, fps.ack_file_name VARCHAR, fps.confirmation_file_name VARCHAR, fps.payment_id VARCHAR, fps.transaction_file_id VARCHAR, fps.rejection_reason VARCHAR);
CREATE TABLE v3_fps_messages_outgoing_confirmed (rowkey VARCHAR NOT NULL PRIMARY KEY, fps.status VARCHAR, fps.rejection_reason VARCHAR);
CREATE TABLE v3_fps_messages_incoming (rowkey VARCHAR NOT NULL PRIMARY KEY,  fps.nostro_sort_code VARCHAR,  fps.nostro_account_number VARCHAR,  fps.amount FLOAT,  fps.receiver_sort_code VARCHAR,  fps.receiver_account_number VARCHAR,  fps.receiver_name VARCHAR,  fps.reference VARCHAR,  fps.sender_name VARCHAR,  fps.status VARCHAR,  fps.created VARCHAR,  fps.transaction_file_name VARCHAR,  fps.sender_account_number VARCHAR,  fps.sender_sort_code VARCHAR);

